import '../css/Signup.css';
import axios from 'axios';

function Signup()
{

  const formSubmit = (event) =>
  {
    event.preventDefault();
    axios.get('http://localhost:4000/login')
      .then(res =>
      {
        let authSuccess = false;
        for (const i in res.data)
        {
          if (res.data[i].userID === inputs.username && res.data[i].password === inputs.password)
          {
            Navigate("/workerList");
            authSuccess = true;
            break;
          }
        }
        if (!authSuccess)
        {
          window.alert("Tài khoản đăng nhập hoặc mật khẩu sai");
          document.getElementsByClassName("loginForm")[0].reset();
        }
      })
      .catch(error => console.log(error));
  }

  return (
    <div class="Signup  d-flex align-items-center" onSubmit={ formSubmit }>
      <div class="container d-flex justify-content-end align-items-center ">
        <form class="form-sign-up col-4 me-5 w-25 border p-3 rounded rounded-3 bg-info bg-gradient shadow-lg">
          <h1 class="text-center mb-5">Register</h1>
          <input type="text" class="form-control mb-4 rounded-pill" placeholder="Your name" />
          <input type="text" class="form-control mb-4 rounded-pill" placeholder="Your email" />
          <input type="text" class="form-control mb-4 rounded-pill" placeholder="Your phone number" />
          <input type="text" class="form-control mb-4 rounded-pill" placeholder="Your username" />
          <input type="password" class="form-control mb-4 rounded-pill" placeholder="Your password" />
          <input type="password" class="form-control mb-4 rounded-pill" placeholder="Re-enter your password" />
          <input type="submit" class="btn btn-primary rounded-pill w-100 my-4" />
          <div class="text-center">Already have an account? <a href="https://www.facebook.com/">Sign in</a></div>
        </form>
      </div>
    </div>
  );
}

export default Signup;
